package hello100;

public class Roonie09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int dir = 10;
		final int b = 9;
		switch(dir){
		case 1:
			System.out.println("A");
			break;
		case 10:
			System.out.println("B1");
			break;
		case b+2:
			System.out.println("B2");
			break;
		
		default:
			System.out.println("X");
		}
	}

}
